    let _OLD_speedMod = yongST ? 0.7 : (hxST||hsST) ? 1.0 : bianST ? 1.4 : 1.0;
    const _OLD_chgPosMod = [0,0.6,0.8,0.95,1.1,1.3,1.6][chg] || 1.0;
