      targetMonthStr = lang==='en' ? monthNamesEN[tMonth-1] : monthNamesCN[tMonth-1];
      fengWangNote = targetFengWang
        ? (lang==='en'
          ? ` (best window: ${monthNamesEN[targetFengWang-1]})`
          : `（${timingEle}逢旺${monthNamesCN[targetFengWang-1]}，最可能应期）`)
        : '';
